import './App.css';
import React, { useState, useEffect } from 'react'
import Usd from './components/Usd';

function App() {

  const [state, setstate] = useState({
    currency_1 : "",
    currency_2 : "",
    input_1 : ""
  })

  const [dataOperationcomplete, setDataSend] = useState({
    general_info : "",
    complete_info : ""
  })

  const obj = {
    usd : 80,
    inr : 1,
    euro : 100,
    aed : 20,
    cad : 30,

  }

  const getData = async (data) =>{
    console.log(data," inside app.js / parent")
    await setstate(data) 

    let my_general_calc = obj[state.currency_1] / obj[state.currency_2]
    let my_complete_calc = my_general_calc * obj[state.input_1]

    setDataSend({
      general_info : `Value of 1 ${state.currency_1} is equivalent to ${my_general_calc} ${state.currency_2}...`,
      complete_info : `Value of ${obj[state.input_1]} ${state.currency_1} is equivalent to ${my_complete_calc} ${state.currency_2}...`
    })

  }

  useEffect(() => {
    console.log(" inside useEffect ")

  },[data_send]);


  return (
    <>
      <h1 className='my-center-style'>Welcome to currency converter!!!</h1>
      <Usd getData={getData} dataOperationcomplete={data_send}/>
    </>
  );
}

export default App;
